package Lec1;

public class Odd_Even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 7;
		if (n > 51) {
			System.out.println("Hello");
		}
		if (n % 2 == 0) {

			System.out.println("Even");
		} else {
			System.out.println("Odd");
		}
		System.out.println("Bye");
	}

}
